$.isInArray = function(item, array) { return !!~$.inArray(item, array); };

//Weather Widget
$(function() {
    iconAppend();
    createWeather2();
    
    $("#weather2 p").click(function(){
        if ("geolocation" in navigator) 
        {
            //get current location if available.
            navigator.geolocation.getCurrentPosition(function(position) {
                //create the widget
                $(".weather").empty();
                createWeather1();
                var curr_loc = position.coords.latitude + ',' + position.coords.longitude
                //change loc. arrow color (to show geolocating)
                $("#weather .location").css({
                   'color' : '#e979f7'
                });

                //shows and hides what the arrow rep.
                $("#weather .location").hover(
                    //show text on mouseover
                    function(){
                        $(this).addClass("geolocation");
                    },
                    //hide text on mouseout
                    function() {
                        $(this).removeClass("geolocation");
                    }
                );
                //call simpleWeather API
                loadweather(curr_loc);
            },
            function(error) {
                if (error.code == error.PERMISSION_DENIED) {
                    alert("Geolocation is disabled. Please enable geolocation in your browser and try again.")
                }
            });
        }
        
        else {
            alert("Your browser does not support geolocation or it is disabled. Please check and try again.")
        }
    });
    
    $("#location-submit").click(function() {
        var location = $("#location-input").val();
        console.log(location);
        if (location == "" || location == null) {
            alert("Enter location.")
        }

        else {
            $(".weather").empty();
            createWeather1();
            loadweather(location);
        }
    });

    //loadweather function for simpleWeather API
    function loadweather(location) {
        $.simpleWeather({
            location: location,
            unit: 'f',
            success: function(weather) {
                //define VARS
                city = weather.city;
                state = weather.region;
                curr_temp = weather.temp;
                high = weather.high;
                low = weather.low;
                image = weather.image;
                tomorrow_avg = (parseInt(weather.forecast[1].high, 10) + parseInt(weather.forecast[1].low, 10)) / 2;
                day_after_avg = (parseInt(weather.forecast[2].high, 10) + parseInt(weather.forecast[2].low, 10)) / 2;
                sunrise = weather.sunrise;
                sunset = weather.sunset;
                code = parseInt(weather.code, 10);
                var night_codes = [27,29,31,33];
                var na = 3200;
                //default day is 1
                var day = 1;
                
                //need to determine what error to throw
                if (code == na) {
                    $(".city").append("Error (3200)");
                }
                
                //no error, got weather
                else {
                    //append approp. vars to divs
                    $(".temp").append(curr_temp);
                    $(".city").append(city);
                    $(".forecast .tomorrow").append("Tomorrow: " + tomorrow_avg);
                    $(".forecast .day_after").append("Day After: " + day_after_avg);

                    //check if day or night                      
                    if ($.isInArray(code, night_codes)) {
                        //If it is night, we want moon
                        day = 0;
                    }
                    
                    //append approp. icon
                    appendIcon(day,code);
                    //append approp. bg from Flickr
                    appendBg(city);
                    
                    //need to add function that displays modal window with sunrise/sunset times, credits (for icons), etc.
                    //it should also include the place to be able to change the location and to call changeLoc approp.
                    
                    //removal button
                    $("#weather .remove").click(function(){
                        $(this).closest('.weather').remove();
                    })
                }
            },
            error: function(error) {
                $("#weather").html('<p>' + error.message + '</p>');
                $("#weather .weather-modal-link").hide();
            }
        });
    }       
});

function appendIcon(day, code) {
    //Codes corresponding to weather 
    //can be found here: https://developer.yahoo.com/weather/archive.html
    var rain_codes = [0,1,2,3,4,8,9,10,11,12,24,35,37,38,39,40,45,47];
    var sun_codes = [19,20,21,22,23,32,34,36];
    var snow_codes = [5,6,7,13,14,15,16,17,18,25,35,41,42,43,46];
    var cloud_codes = [26,28,30,44];
    
    //first we set the approp. icon
    //options for icons
    /*
    sunny
    rainy_day
    rainy_night
    cloudy_day
    cloudy_night
    snowing_day
    snowing_night
    sunrise
    sunset
    night (27 = mostly cloudy, 29 = partly cloudy, 31 = clear, 33 = fair)
    */
    
    //if its day
    if (day == 1) {
        //get the searchterm for getIcon function
        if ($.isInArray(code, rain_codes)) {
            getIconSearchTerm = "rainy_day"
        }

        else if ($.isInArray(code, sun_codes)) {
            getIconSearchTerm = "sunny"
        }

        else if ($.isInArray(code, snow_codes)) {
            getIconSearchTerm = "snowing_day"
        }

        else if ($.isInArray(code, cloud_codes)) {
            getIconSearchTerm = "cloudy_day"
        }
    }
    
    else {
        if (code == 27 || code == 29) {
            getIconSearchTerm = "cloudy_night";
        }

        else {
            getIconSearchTerm = "night";
        }
    }
    
    var icon_html = getIcon(getIconSearchTerm);
    //get the icon and append it.
    $("#weather .icon").empty().append(icon_html);
}

function appendBg(city) {
    //Now pull a bg from flickr
    var flickrUrl = "https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=f57388ba912e09eabb5a500be18ebf24&tags=%22" + city + "%22&sort=relevance&format=json&nojsoncallback=1&per_page=1";

    //JSON function for flickrAPI call
    $.getJSON(flickrUrl, function(data)
    { 
        $.each(data.photos.photo, function(i, item)
        { 
            var background_image = "https://farm" + item.farm + ".static.flickr.com/" + item.server
            + "/" + item.id + "_" + item.secret + "_b.jpg";

            console.log(background_image);

            if (background_image != "undefined") {
                $(".weather").css('background-image', 'url("' + background_image + '")');
            }

            //if no flickr image
            else {
                $(".weather").css('background', 'skyBlue');
            }
        });
    });  
}
        
